                if (point === 1) {
                    this.x1 = constrain(x1 + deltaLeft)
                    this.y1 = y1 + deltaTop
                } else if (point === 2) {
                    this.x2 = constrain(x2 + deltaLeft)
                    this.y2 = y2 + deltaTop
                }
