    Person.prototype.shower = function() {
        this.dirtFactor = 0
    }

    // 只给Gavin设置新酷炫着装方法
    gk.beCool = function() {
        this.clothing = 'tinfoil'
    }

    gk.beCool()
    gk.shower()
    console.log('Fashionable ' + gk + ' at ' +
        gk.getAge() + ' years old is now wearing ' +
        gk.clothing + ' with dirt factor ' +
        gk.dirtFactor)

    // 使用原型方法设置Gavin的公有属性
    gk.amputate()
    gk.showLegs()
    // Lisa的依旧维持原公有属性
    lk.showLegs()

    // 又50年过去了
    gk.muchTimePasses()
    // Gavin去世了，不再进食
    gk.eat()
}

runLives()
```

