    n = 0:FFT_points-1;
    t = n/sampleRate; %
    f_All = n*sampleRate/FFT_points; %

    %
    signal = 2*sin(2*pi*10*t)+1*sin(2*pi*20.25*t)+0.2*randn(size(t));

    %Fourier
    amplitude_All = abs(fft(signal,FFT_points))*2/FFT_points;

    f = f_All(1:FFT_points/2);
    amplitude = amplitude_All(1:FFT_points/2);
