                        }] : [404, {
                            code: 404,
                            msg: 'Not Found',
                            data: null
                        }]
                } else {
                    response = [500, {
                        code: 500,
                        msg: 'Internal Server Error',
                        data: null
                    }]
                }

                setTimeout(() => {
                    if (response[0] === 200) {
                        resolve(response)
                    } else {
                        reject(response)
                    }
                }, Math.floor(200 + Math.random() * 800))
            })
        })
    }
}
