<script type="text/javascript" async src="https://cdnjs.cloudflare.com/ajax/libs/mathjax/2.7.5/MathJax.js?config=TeX-MML-AM_CHTML"></script>
<script src="/scripts/vue.min.js"></script>
<script>
const app = new Vue({
	el: '#triangle',

	data: {
		w: 0,
		h: 0,
		x: 0,
		y: 0,
		x1: 0,
		y1: 0,
		x2: 0,
		y2: 0,
		x3: 0,
		y3: 0
	},

	computed: {
		d() {
			const t = this
			return `M${t.x1},${t.y1} L${t.x2},${t.y2} L${t.x3},${t.y3} Z`
		},
		inTriangle() {
			const t = this
			const A = [t.x1, t.y1]
			const B = [t.x2, t.y2]
			const C = [t.x3, t.y3]
			const P = [t.x, t.y]
			const sub = ([a, b], [d, e]) => [a - d, b - e]
			const dot = ([a, b], [d, e]) => a * d + b * e
			const uv = () => {
				const c = sub(C, A)
				const b = sub(B, A)
				const p = sub(P, A)
				const cc = dot(c, c)
				const bc = dot(b, c)
				const pc = dot(c, p)
				const bb = dot(b, b)
				const pb = dot(b, p)
				const denom = cc * bb - bc * bc
				const u = (bb * pc - bc * pb) / denom
				const v = (cc * pb - bc * pc) / denom
				return [u, v]
			}
			const [u, v] = uv()
			return (u >= 0) && (v >= 0) && (u + v <= 1)
		}
	},

	methods: {
		onClick(event) {
			const el = document.querySelector('#triangle')
			const { left, top } = el.getBoundingClientRect()
			const { clientX, clientY } = event
			this.x = clientX - left
			this.y = clientY - top
		}
	},

	mounted() {
		this.w = Math.round(document.querySelector('#triangle').parentNode.clientWidth)
		this.h = Math.round(this.w * 0.4)
		this.x = Math.round(this.w / 2)
		this.y = Math.round(this.h / 2)

		const { w, h } = this
		const f = (max, min) => Math.floor(min + Math.random() * (max - min))
		this.x1 = f(0, w / 3)
		this.y1 = f(0, h / 3)
		this.x2 = f(w * 2 / 3, w)
		this.y2 = f(0, h / 3)
		this.x3 = f(w / 3, w * 2 / 3)
		this.y3 = f(h * 2 / 3, h)
	}
})
</script>
