const width = Math.max(document.querySelector('svg#d3').parentNode.clientWidth, 600)
let height
const dx = 20
let dy

const hierarchy = d3.hierarchy(data)
// hierarchy.sort((a, b) => (a.height - b.height) || a.data.name.localeCompare(b.data.name))

dy = width / (hierarchy.height + 1) - 5

const root = d3.tree().nodeSize([dx, dy])(hierarchy)
// const root = d3.cluster()

let min = Infinity
let max = -min
root.each(d => {
    if (d.x > max) max = d.x
    if (d.x < min) min = d.x
})
height = max - min + dx * 2

const svg = d3.select('svg#d3')
    .attr('width', width)
    .attr('height', height)
    .attr('viewBox', `0 0 ${width} ${height}`)

const g = svg.append('g')
    .attr('font-size', 12)
    .attr('transform', `translate(${dy / 3}, ${dx - min})`)

const link = g.append('g')
    .attr('fill', 'none')
    .attr('stroke', '#83887c')
    .attr('stroke-width', 1.5)
    .selectAll('path')
    .data(root.links())
    .enter().append('path')
    .attr('d', d => `
        M${d.target.y}, ${d.target.x}
        C${d.source.y + dy / 2}, ${d.target.x}
        ${d.source.y + dy / 2}, ${d.source.x}
        ${d.source.y}, ${d.source.x}
    `)

const node = g.append('g')
    .attr('stroke-linejoin', 'round')
    .attr('stroke-width', 3)
    .selectAll('g')
    .data(root.descendants())
    .enter().append('g')
    .attr('transform', d => `translate(${d.y},${d.x})`)

node.append('circle')
    .attr('fill', '#83887c')
    .attr('r', 2.5)

node.append('text')
    .attr('dy', '0.3em')
    .attr('x', d => d.children ? -6 : 6)
    .text(d => d.data.name)
    .filter(d => d.children)
    .attr('text-anchor', 'end')
    .clone(true).lower()
    .attr('stroke', '#f2f6ed')
</script>
